//facial-rec.js
import { useEffect, useState, useRef } from 'react';
import * as faceLandmarksDetection from '@tensorflow-models/face-landmarks-detection';
import '@tensorflow/tfjs-backend-webgl';

export const useFaceDetector = (videoRef) => {
    const [detector, setDetector] = useState(null);
    const detectionInterval = useRef(null);
    const audioRef = useRef(new Audio('/ringtone-126505.mp3'));
    const [playSound, setPlaySound] = useState(false);

    useEffect(() => {
        console.log('Loading model...');
        const loadModel = async () => {
            const model = faceLandmarksDetection.SupportedModels.MediaPipeFaceMesh;
            const detectorConfig = {
                runtime: 'tfjs',
                solutionPath: 'https://cdn.jsdelivr.net/npm/@mediapipe/face_mesh',
            };
            const detector = await faceLandmarksDetection.createDetector(model, detectorConfig);
            setDetector(detector);
            console.log('Model loaded.');
        };

        loadModel();
    }, []);

    useEffect(() => {
        console.log('Setting up interval...');
        const checkFaces = async () => {
            console.log('Checking faces...');
            if (videoRef.current && !videoRef.current.paused && !videoRef.current.ended && detector) {
                console.log('Video is playing, detecting faces...');
                const faces = await detector.estimateFaces(videoRef.current, { flipHorizontal: false });
                if (faces.length > 0) {
                    console.log(faces);
                    try {
                        const faceNames = faces[0]?.keypoints.filter(item => item.hasOwnProperty('name'));
                        const rightEye = faceNames.filter(items => items["name"] == "rightEye")[2].x;
                        const leftEye = faceNames.filter(items => items["name"] == "leftEye")[2].x;
                        const faceOvalRight = faceNames.filter(items => items["name"] == "faceOval")[26].x;
                        const faceOvalLeft = faceNames.filter(items => items["name"] == "faceOval")[15].x;

                        console.log(`Right Eye: ${rightEye}, Left Eye: ${leftEye}, Face Oval Right: ${faceOvalRight}, Face Oval Left: ${faceOvalLeft}`);

                        if (rightEye && leftEye && faceOvalRight && faceOvalLeft) {
                            console.log("All keypoints found.");
                            console.log(`Condition check: Right Eye + 50 > Face Oval Right (${rightEye + 50} > ${faceOvalRight})`);
                            console.log(`Condition check: Left Eye - 50 < Face Oval Left (${leftEye - 50} < ${faceOvalLeft})`);
                            if (rightEye + 50 > faceOvalRight || leftEye - 50 < faceOvalLeft) {
                                console.log("Out of bounds or close, should play sound.");
                                if (!playSound) {
                                    setPlaySound(true);
                                    audioRef.current.play();
                                    console.log("Condition met, playing sound.");
                                }
                            } else {
                                console.log("Within bounds, should pause sound.");
                                if (playSound) {
                                    setPlaySound(false);
                                    audioRef.current.pause();
                                    audioRef.current.currentTime = 0;
                                    console.log("Condition not met, stopping sound.");
                                }
                            }
                        } else {
                            console.log("One or more keypoints are undefined.");
                        }
                    } catch (error) {
                        console.error("Error processing facial landmarks:", error);
                    }
                } else {
                    console.log("No faces detected.");
                }
            }
        };

        if (detector && videoRef.current) {
            checkFaces(); // Run immediately on load
            detectionInterval.current = setInterval(checkFaces, 3000); // Changed to 30 seconds as per requirement
        }

        return () => {
            if (detectionInterval.current) {
                clearInterval(detectionInterval.current);
            }
            if (audioRef.current) {
                audioRef.current.pause();
            }
            console.log('Cleanup done.');
        };
    }, [detector, videoRef, playSound]); // Removed playSound from dependencies to prevent re-triggering on its change

    return null;
};
