//facial-rec.js
import { useEffect, useState, useRef } from 'react';
import * as faceLandmarksDetection from '@tensorflow-models/face-landmarks-detection';
import '@tensorflow/tfjs-backend-webgl';

export const useFaceDetector = (videoRef) => {
    const [detector, setDetector] = useState(null);
    const detectionInterval = useRef(null);
    const audioRef = useRef(new Audio('/ringtone-126505.mp3'));

    useEffect(() => {
        const loadModel = async () => {
            const model = faceLandmarksDetection.SupportedModels.MediaPipeFaceMesh;
            const detectorConfig = {
                runtime: 'tfjs', // or 'tfjs'
                solutionPath: 'https://cdn.jsdelivr.net/npm/@mediapipe/face_mesh',
            };
            const detector = await faceLandmarksDetection.createDetector(model, detectorConfig);
            setDetector(detector);
        };

        loadModel();
    }, []);

    useEffect(() => {
        const checkFaces = async () => {
            if (videoRef.current && !videoRef.current.paused && !videoRef.current.ended) {
                const faces = await detector.estimateFaces(videoRef.current, { flipHorizontal: false });
                try {
                    const faceNames = faces[0]['keypoints'].filter(items => items.hasOwnProperty('name'));
                    const rightEye = faceNames.find(item => item.name === "rightEye")?.x;
                    const leftEye = faceNames.find(item => item.name === "leftEye")?.x;
                    const faceOvalRight = faceNames.filter(item => item.name === "faceOval")[26]?.x;
                    const faceOvalLeft = faceNames.filter(item => item.name === "faceOval")[15]?.x;

                    if (rightEye && leftEye && faceOvalRight && faceOvalLeft) {
                        if (rightEye + 50 > faceOvalRight || leftEye - 50 < faceOvalLeft) {
                            if (!playSound) {
                                setPlaySound(true);
                                audioRef.current.play();
                            }
                        } else {
                            if (playSound) {
                                setPlaySound(false);
                                audioRef.current.pause();
                                audioRef.current.currentTime = 0;
                            }
                        }
                    }
                } catch (error) {
                    console.error("Error processing facial landmarks:", error);
                }
            }
        };

        if (detector && videoRef.current) {
            checkFaces(); // Run immediately on load
            detectionInterval.current = setInterval(checkFaces, 30000); // Run every 30 seconds
        }

        return () => {
            if (detectionInterval.current) {
                clearInterval(detectionInterval.current);
            }
            audioRef.current.pause();
        };
    }, [detector, videoRef, playSound]);

    return null;
};
